mapper.select:
SELECT  id,module,userId,usingTime
FROM function_using
WHERE module = #{module}

SELECT  id,module,userId,usingTime
FROM function_using
WHERE userId = #{id}

AND module = #{module}

SELECT id,module,userId,usingTime
FROM function_using
WHERE usingTime > #{timeStart}
AND usingTime < #{timeEnd}

AND    module = #{module}


AND    userId = #{userId}
