mapper.select:
SELECT  id,nickname,password,salt,head,register_date,last_login_date,login_count,grade,ban
FROM shenhui_user
WHERE register_date > #{timeStart}
AND register_date < #{timeEnd}

    id = #{id},

mapper.update:
UPDATE shenhui_user SET
    ban = #{type}
WHERE id = #{id}

UPDATE shenhui_user SET
    password = #{password},
    salt = #{salt}
WHERE id = #{id}
